import {useState,useEffect } from 'react'
import Edit from "./components/Edit"
import List from "./components/List"
import Footer from "./components/Footer"
import axios from 'axios'
import './index.css'




// POST REQUEST
function addTodo() {
  axios
    .post('https://jsonplaceholder.typicode.com/posts', {
      title: 'New Todo',
      completed: false
    })
    .catch(err => console.error(err));
}

// PUT/PATCH REQUEST
function updateTodo() {
  axios
    .patch('https://jsonplaceholder.typicode.com/posts', {
      title: 'Updated Todo',
      completed: true
    })
    .catch(err => console.error(err));
}

// DELETE REQUEST
function removeTodo() {
  axios
    .delete('https://jsonplaceholder.typicode.com/posts')
    .catch(err => console.error(err));
}




const Home = () => {
    const [data, setData] = useState([])
    useEffect(async () => {
      const result = await 
      axios.get(
        'https://jsonplaceholder.typicode.com/posts?_start=0&_limit=6',
      );
      setData(result.data);
    }
    );
    const newPost = {
      userId: 1,
      title: 'A new post',
      body: 'This is the body of the new post'
  };
    const sendPostRequest = async () => {
      try {
          const resp = await axios.post('https://jsonplaceholder.typicode.com/posts', newPost);
          console.log(resp.data);
      } catch (err) {
          // Handle Error Here
          console.error(err);
      }
  };
  
  sendPostRequest();
    
  //   useEffect(() =>{
  //   axios
  //   .get('https://jsonplaceholder.typicode.com/posts')
  //   .then(res => {
  //     console.log(res)
  //     setData(result.data);
  //   })
  //   .catch(err =>{
  //     console.log(err)
  //   })
  // })
    
    return ( <div className="app">
        <Edit add={setData}  />
        <List listData = {data} deleteData ={setData} />
        <div>
        <Footer/>
        </div>
    </div>
    )
    
}
export default Home
