function deleteItem(){
deleteData(function(prev){
    return prev.filter(item => item.id !== id)
})
}

    return( <div className="item">
<div> 
    <p>Posted By {user}</p>
    <p>{title}</p>
    <p>{note}</p>
    <p> {`${date} ${time}`}</p>
</div>
<button type="button" onClick = {deleteItem} className ="btn btn-outline-danger">Delete </button>
    </div>
    )
}
