import { useState } from "react"
import {v4} from 'uuid'

const Edit = ({ add }) => {
    const[user,setUser] = useState("")
    function userChange(e){
        setUser(e.target.value)
    }
    const[title,setTitle] = useState("")
    function titleChange(e){
        setTitle(e.target.value)
    }
    const[note,setNote] = useState("")
    function noteChange(e){
        setNote(e.target.value)
    }
    const[date,setDate] = useState("")
    function dateChange(e){
        setDate(e.target.value)
    }
    const[time,setTime] = useState("")
    function timeChange(e){
        setTime(e.target.value)
    }
    function addItem(){
        add(function(prevData){
            return [
            {
                id: v4(),
                user,title,note,date,time
            },
            ...prevData,]
    })
}

    console.log(user,title, note, date, time)
    return <div>
        <h1>React Blog</h1>
        <p>User</p>
        <input type = "text" value = {title} onChange = {userChange}></input>
        <p>Title</p>
        <input type = "text" value = {title} onChange = {titleChange}></input>
        <p>Note</p>
        <input type = "text" value = {note} onChange = {noteChange}></input>
        <p>Date: </p>
        <input type = "date" value = {date} onChange = {dateChange}></input>
        <p>Time: </p>
        <input type = "time" value = {time} onChange = {timeChange}></input>
        <p> </p>
        <div class="d-grid gap-2">
        <button onClick ={addItem} className="btn btn-outline-dark">Add</button>
        </div>
    </div>
}
export default Edit
