import { useState } from "react"
import {v4} from 'uuid'
const Edit = ({ add }) => {

    const[note,setNote] = useState("")
    function noteChange(e){
        setNote(e.target.value)
    }
    const[date,setDate] = useState("")
    function dateChange(e){
        setDate(e.target.value)
    }
    const[time,setTime] = useState("")
    function timeChange(e){
        setTime(e.target.value)
    }
    function addItem(){
        add(function(prevData){
            return [...prevData,
            {
                id: v4(),
                note,date,time
            }]
    })
}

    console.log(note, date, time)
    return <div>
        <h1>Memo</h1>
        <p>Things to do</p>
        <input type = "text" value = {note} onChange = {noteChange}></input>
        <p>Date: </p>
        <input type = "date" value = {date} onChange = {dateChange}></input>
        <p>Time: </p>
        <input type = "time" value = {time} onChange = {timeChange}></input>
        <button onClick ={addItem} className="add">Add</button>
    </div>
}
export default Edit
